import React, { useState } from 'react';

const BookingModal = ({ closeModal }) => {
  const [bookingData, setBookingData] = useState({});

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setBookingData((prevData) => ({ ...prevData, [name]: value }));
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // Process the bookingData (e.g., submit a form, send an API request, etc.)
    console.log('Booking data:', bookingData);
    // Close the modal after processing the booking
    closeModal();
  };

  return (
    <div>
        <h2 className='font-semibold text-lg uppercase text-center mb'>
            Book a Shoot
        </h2>
        <form onSubmit={handleSubmit}>
            <label htmlFor="name">Name:</label>
            <input
            type="text"
            id="name"
            name="name"
            onChange={handleInputChange}
            required
            />
            {/* Add more input fields for booking information */}
            <button type="submit">Submit</button>
        </form>
    </div>
  );
};

export default BookingModal;
