import React, { useState } from 'react';
import Modal from 'react-modal';
import { Link } from 'react-router-dom';
import { FiMenu } from 'react-icons/fi'; // Example: using react-icons

import BookingModal from './BookingModal';

const Header = () => {
  const [modalOpen, setModalOpen] = useState(false);
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false); // Track mobile menu visibility

  const openModal = () => {
    setModalOpen(true);
  };

  const closeModal = () => {
    setModalOpen(false);
  };

  const toggleMobileMenu = () => {
    setMobileMenuOpen(!mobileMenuOpen);
  };

  const scrollToSection = (sectionId) => {
    const homeUrl = new URL(window.location.origin);
    homeUrl.hash = `#${sectionId}`;
    window.location.href = homeUrl.toString();
  };

  return (
    <header className='sticky top-0 flex justify-between items-center py-10 px-16 z-[1000] backdrop-blur backdrop-filter mobile:hidden'>
      <section>
        <Link to='/'>
          <h2 className='font-bold text-2xl hover:scale-105 transition-all duration-300'>
            Summit Photography
          </h2>
        </Link>
      </section>
      <section className='w-[40%]'>
        {/* Render mobile menu icon and menu */}
        <div className='flex items-center'>
          <button
            className='p-2 hover:scale-105 transition-all duration-300'
            onClick={toggleMobileMenu}
          >
            <FiMenu size={24} /> {/* Replace with your desired hamburger icon */}
          </button>
          {/* Mobile menu */}
          {mobileMenuOpen && (
            <ul className='flex flex-col absolute top-full right-0 mt-2 py-2 bg-white rounded-md shadow'>
              <li className='cursor-pointer' onClick={() => scrollToSection('home')}>
                Home
              </li>
              <li className='cursor-pointer' onClick={() => scrollToSection('about')}>
                About
              </li>
              <li className='cursor-pointer' onClick={() => scrollToSection('portfolio')}>
                Portfolio
              </li>
              <li className='cursor-pointer' onClick={() => scrollToSection('feedback')}>
                Feedback
              </li>
              <li className='cursor-pointer' onClick={() => scrollToSection('contact')}>
                Contact
              </li>
            </ul>
          )}
        </div>
      </section>
      <section>
        <button
          onClick={openModal}
          className='border border-black p-2 hover:scale-105 transition-all duration-300'
        >
          BOOK A SHOOT
        </button>
      </section>
      <Modal
        isOpen={modalOpen}
        onRequestClose={closeModal}
        contentLabel='Book A Shoot'
        className='modal fixed transition-transform transform translalate-x-[50%] translalate-y-[50%] bg-white p-10 rounded-md max-w-[400px] w-full'
        overlayClassName='modal-overlay fixed top-0 bottom-0 left-0 right-0 bg-black/50 flex justify-center items-center'
        ariaHideApp={false}
      >
        <BookingModal closeModal={closeModal} />
      </Modal>
    </header>
  );
};

export default Header;
