import React, { useState } from 'react';
import Modal from 'react-modal';
import { Link } from 'react-router-dom';
import BookingModal from './BookingModal';

const Header = () => {
  const [modalOpen, setModalOpen] = useState(false);
  const [menuOpen, setMenuOpen] = useState(false);

  const openModal = () => {
    setModalOpen(true);
  };

  const closeModal = () => {
    setModalOpen(false);
  };

  const toggleMenu = () => {
    setMenuOpen(!menuOpen);
  };

  const scrollToSection = (sectionId) => {
    const homeUrl = new URL(window.location.origin);
    homeUrl.hash = `#${sectionId}`;
    window.location.href = homeUrl.toString();
  };

  return (
    <header className='sticky top-0 py-6 px-8 z-[1000] backdrop-blur backdrop-filter'>
      <div className='flex justify-between items-center'>
        <div>
          <Link to='/'>
            <h2 className='font-bold text-2xl hover:scale-105 transition-all duration-300'>
              Summit Photography
            </h2>
          </Link>
        </div>
        <div className='sm:hidden'>
          <button
            onClick={toggleMenu}
            className='border border-black px-4 py-2 hover:scale-105 transition-all duration-300'
          >
            <svg
              xmlns='http://www.w3.org/2000/svg'
              viewBox='0 0 24 24'
              fill='none'
              stroke='currentColor'
              strokeWidth='2'
              strokeLinecap='round'
              strokeLinejoin='round'
              className='w-6 h-6'
            >
              <line x1='3' y1='12' x2='21' y2='12' />
              <line x1='3' y1='6' x2='21' y2='6' />
              <line x1='3' y1='18' x2='21' y2='18' />
            </svg>
          </button>
        </div>
      </div>
      {menuOpen && (
        <div className='mt-4'>
          <ul className='flex flex-col items-center space-y-4'>
            <li>
              <button onClick={() => scrollToSection('home')}>Home</button>
            </li>
            <li>
              <button onClick={() => scrollToSection('about')}>About</button>
            </li>
            <li>
              <button onClick={() => scrollToSection('portfolio')}>
                Portfolio
              </button>
            </li>
            <li>
              <button onClick={() => scrollToSection('feedback')}>
                Feedback
              </button>
            </li>
            <li>
              <button onClick={() => scrollToSection('contact')}>
                Contact
              </button>
            </li>
          </ul>
        </div>
      )}
      <Modal
        isOpen={modalOpen}
        onRequestClose={closeModal}
        contentLabel='Book A Shoot'
        className='modal fixed transition-transform transform translate-x-[-50%] translate-y-[-50%] bg-white p-10 rounded-md max-w-[400px] w-full'
        overlayClassName='modal-overlay fixed top-0 bottom-0 left-0 right-0 bg-black/50 flex justify-center items-center'
        ariaHideApp={false}
      >
        <BookingModal closeModal={closeModal} />
      </Modal>
    </header>
  );
};

export default Header;
