import React from 'react';
import { useForm } from 'react-hook-form';
import { toast } from 'react-toastify';

const BookingModal = ({ closeModal }) => {
  const { register, handleSubmit, formState: { errors } } = useForm();

  const onSubmit = (data) => {
    // Process the bookingData (e.g., submit a form, send an API request, etc.)
    console.log('Booking data:', data);

    // Show a success notification
    toast.success('Form submitted successfully!', {
      position: toast.POSITION.TOP_RIGHT
    });

    // Close the modal after processing the booking
    closeModal();
  };

  return (
    <div className='space-y-10'>
      <h2 className='font-semibold text-xl uppercase text-center'>
        Book a Shoot
      </h2>
      <form
        id='bookingForm'
        method='POST'
        className='w-full flex flex-col space-y-4'
        onSubmit={handleSubmit(onSubmit)}
      >
        <div>
          <label htmlFor='name' className='text-gray-800 font-semibold'>
            Name
          </label>
          <input
            type='text'
            id='name'
            name='name'
            className={`w-full border ${errors.name ? 'border-red-500' : 'border-gray-300'} px-4 py-2 rounded-md focus:outline-none focus:border-primary-focus`}
            {...register('name', { required: 'Name is required' })}
          />
          {errors.name && <span className='text-red-500'>{errors.name.message}</span>}
        </div>
        {/* Add more form input fields */}
        <button
          type='submit'
          className='bg-primary hover:bg-primary-focus text-white font-semibold py-2 px-6 rounded-md'
        >
          Submit
        </button>
      </form>
    </div>
  );
};

export default BookingModal;
